import { NavLink } from 'react-router-dom';
import * as React from 'react';
import { Box } from 'grommet';
import { Icon } from '../../Styled';

export class CompactNav extends React.Component {
  public render() {
    return (
      <Box direction="column" style={{ color: '#FFFFFF' }} gap="small" background={{ color: "#191C1B" }} fill>
        <NavLink to="/home" className="nav-link">
          <Icon name="Logo" size={24} />
        </NavLink>
        <NavLink to="/stations" className="nav-link">
          <Icon name="RadioAntenna1" size={24} />
        </NavLink>
        <NavLink to="/playlists" className="nav-link">
          <Icon name="ListBullets" size={24} />
        </NavLink>
      </Box>
    );
  }
}
