import * as React from 'react';

import { Heading, Paragraph } from 'grommet';

export default function CompactNotAvailable() {
  return (
    <div>
      <Heading textAlign="center" size="small">This page is not available in compact mode</Heading>
      <Paragraph textAlign="center">Try making the player window larger to use other pages.</Paragraph>
    </div>
  );
}
