import { Motion, spring } from 'react-motion';
import { Handle, Wrapper } from './Styles';
import * as React from 'react';
import { Icon, PretzelColors } from '../../Styled';

interface State {
  open: boolean;
}
export class Drawer extends React.Component<{}, State> {
  state = {
    open: false,
  };

  public render() {
    return (
      <Motion style={{ y: spring(this.state.open ? 16 : 100) }}>
        {({ y }) => (
          <Wrapper
            className="no-drag"
            style={{
              transform: `translateY(${y}%)`,
            }}
          >
            <Handle onClick={this.handleHandleClick}>
              <Icon name="more" color={PretzelColors.baseWhite} size={32} />
            </Handle>
            {this.props.children}
          </Wrapper>
        )}
      </Motion>
    );
  }
  private handleHandleClick = () => {
    this.setState({ open: !this.state.open });
  };
}
