import styled from 'styled-components/macro';
import { PretzelColors } from '../../Styled';

export const Wrapper = styled.div`
  height: 74px;
  width: 100%;
  ${'' /* font-size: 12px; */}
  display: flex;
  flex-direction: column;
  align-items: center;
  position: absolute;
  bottom: 12px;
  background: ${PretzelColors.baseBackground};
  z-index: 7;
`;

export const Handle = styled.div`
  width: 100%;
  height: 12px;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
`;
