import * as React from 'react';
import { Box, Heading } from 'grommet';
import styled from 'styled-components/macro';
import { PlaceholderLink } from '../../../Styled';

interface PublicProps<Thing> {
  title: string;
  items: Thing[] | null;
}

export const ItemList = styled.ul`
  list-style: none;
  padding-left: 16px;
  margin-top: 0;
`;

export const ListItems = styled.li`
  margin-bottom: 8px;
`;

export class InfoLinks<Thing> extends React.Component<PublicProps<Thing>> {
  public render() {
    const items = this.props.items === null ? this.renderPlaceholders() : this.props.items;
    return (
      <Box basis="1/2">
        <Heading level="3" size="small" color="#f8f8f8">
          {this.props.title}
        </Heading>
        <ItemList>{items}</ItemList>
      </Box>
    );
  }

  private renderPlaceholders() {
    return [1, 2, 3].map(i => <PlaceholderLink key={i} />);
  }
}
