export interface FeaturedAlbum {
  id: string;
  artworkGuid: string;
  name: string;
  artists: string;
  stats: {
    totalCount: number;
    duration: number;
  };
}

export interface HomeExternalLink {
  name: string;
  url: string;
}

export interface HomeData {
  featuredTitle: string;
  featured: [FeaturedAlbum];
  gettingStarted: [HomeExternalLink];
  comingSoon: [HomeExternalLink];
}

export interface HomeDataQuery {
  homeData: HomeData;
}
