import * as React from 'react';
// @ts-ignore
import { Scrollbars } from 'react-custom-scrollbars';
import { CSSProperties } from 'react';

interface PublicProps {}

type Props = PublicProps;

export class PretzelScrollbarsPresentation extends React.Component<Props> {
  public render() {
    return (
      <Scrollbars
        className="no-drag"
        hideTracksWhenNotNeeded={true}
        style={{
          marginTop: 10,
          height: '100%',
          overflowX: 'hidden',
        }}
        renderView={({ style, ...props }: { style: CSSProperties }) => (
          <div {...props} style={{ ...style, marginBottom: 1, overflowX: 'hidden' }} />
        )}
      >
        {this.props.children}
      </Scrollbars>
    );
  }
}

export const PretzelScrollbars = PretzelScrollbarsPresentation;
