import * as React from 'react';
import { CompactOnly, LibraryOnly } from '../../Util/responsive';
import { PretzelPage } from '../PretzelPage/PretzelPage';
import { CompactPage } from '../CompactPage/CompactPage';

export default function ResponsivePage(props: any) {
  return (
    <>
      <CompactOnly>
        <CompactPage {...props} />
      </CompactOnly>
      <LibraryOnly>
        <PretzelPage {...props} />
      </LibraryOnly>
    </>
  );
}
