import { BasicTrack } from '../../Core/Player/Queries/schema';

export interface Track {
  guid: string;
  artwork_guid: string;
  descriptor: string;
  display_artists: string;
  explicit: boolean;
  in_rotation: boolean;
  instrumental: boolean;
  isrcs: string[];
  mix: boolean;
  popularity: string;
  song_request_enabled: boolean;
  state: string;
  title: string;
  "youtube_safe?": boolean;
}

export interface Artist {
  guid: string;
  artwork_guid: string;
  name: string;
  popularity: string;
}

export interface Album {
  guid: string;
  artists: string;
  artwork_guid: string;
  popularity: string;
  title: string;
  upcs: string[];
}

interface TrackEdge {
  node: BasicTrack;
}

export interface SearchQuery {
  id: string;
  search: {
    tracks: {
      edges: TrackEdge[];
    };
  };
}
