import * as React from 'react';
import { Box, Heading } from 'grommet';
import { PretzelColors } from '../../../Styled';

interface PublicProps {
  title: string;
}

type Props = PublicProps;

export class SettingsPagePresentation extends React.Component<Props> {
  public render() {
    return (
      <Box direction="column">
        <Box pad={{ left: '24px' }}>
          <Heading level="4" size="small" color={PretzelColors.brandGreen}>
            {this.props.title.toUpperCase()}
          </Heading>
        </Box>
        <Box direction="column" pad={{ left: '24px' }}>
          {this.props.children}
        </Box>
      </Box>
    );
  }
}

export const SettingsSection = SettingsPagePresentation;
