import * as React from 'react';
import { SettingsSection } from '../../Components/SettingsSection';
import { AudioQualitySelector } from './Components/AudioQualitySelector/AudioQualitySelector';
import { AudioOutputSelector } from './Components/AudioOutputSelector/AudioOutputSelector';

interface PublicProps {}

type Props = PublicProps;

interface State {}

export class GeneralPresentation extends React.Component<Props, State> {
  state = {};

  public render() {
    return (
      <SettingsSection title="Audio">
        <AudioQualitySelector />
        <AudioOutputSelector />
      </SettingsSection>
    );
  }
}

export const Audio = GeneralPresentation;
