import React from 'react';
import styled from 'styled-components';
import { PretzelColors } from '../../../../../Styled';

const Wrapper = styled.div`
  margin: 0 10px;
`;

const AddKeybind = styled.div`
  padding: 5px 16px;
  border-radius: 4px;
  border: 1px solid ${PretzelColors.baseWhite};
  color: ${PretzelColors.baseWhite};
  background: transparent;
  &:hover {
    color: ${PretzelColors.baseBackground2};
    background: ${PretzelColors.baseWhite};
  }
  font-size: 14px;
  cursor: pointer;
  -webkit-app-region: no-drag !important;
  display: inline-block;
  margin: 15px 0 0 -10px;
`;

interface Props {
  addAKeybind: (e:React.MouseEvent) => void,
}

const AddKeybindButton = (props: Props) => (
  <Wrapper>
    <AddKeybind onClick={props.addAKeybind}>
      Add a keybind
    </AddKeybind>
  </Wrapper>
);

export default AddKeybindButton;
