import { SongRequest } from '../../Core/Player/Queries/schema';

export interface SRArtist {
  id: string;
  name: string;
}

export interface SRTrack {
  id: string;
  artworkGuid: string;
  title: string;
  artists: SRArtist[];
}

export interface SongRequestWithTrack extends SongRequest {
  track: SRTrack;
}

export interface SRUser {
  id: string;
  songRequestsEnabled: boolean;
  songRequestQueue: SongRequestWithTrack[];
}

export interface SongRequestQuery {
  currentUser: SRUser;
}
