export interface SetTrackLikedInput {
  trackId: string;
  value: boolean;
}

export interface SetTrackLikedMutation {
  likeTrack: {
    track: {
      self: {
        liked: boolean;
      };
    };
  };
}

export interface SetTrackBlacklistedInput {
  trackId: string;
  value: boolean;
}

export interface SetTrackBlacklistedMutation {
  blacklistTrack: {
    track: {
      self: {
        blacklisted: boolean;
      };
    };
  };
}
