import styled from 'styled-components/macro';
import { Night, PretzelColors } from '../../../../Styled';

export const ProgressBar = styled.div`
  height: 4px;
  margin: 0 6px 1px 6px;
  flex: 1;
  background: ${Night.textDefaultButtons};
  overflow: hidden;
  position: relative;
  border-radius: 2px;
`;

export const ProgressFill = styled.div`
  height: 4px;
  background: linear-gradient(90deg, ${PretzelColors.gradientStart}, ${PretzelColors.gradientEnd});
  transition: transform 1s linear;
  transform: translateX(${(props: { fill: number }) => -100 + props.fill}%);
`;

export const Time = styled.p`
  margin: 5px;
  color: ${Night.textMuted};
  font-size: 12px;
  -webkit-app-region: no-drag;
`;
