import styled from 'styled-components/macro';

export const PlayerWrapper = styled.div`
  display: flex;
  flex-direction: column;
  width: 250px;
  ${'' /* flex: 1; */}
  justify-content: space-between;
  @media (max-width: 470px), (max-height: 470px) {
    width: 100%;
    height: 100%;
  }
  @media (width: 304px), (height: 467px) {
    height: auto;
  }
`;
