export interface Settings {
  allowExplicit: boolean;
  //  audioVolume: number; // Not used yet
  //  autoplayOnLoad: boolean; // ?
  dailyAffirmations: boolean;
  instrumentalOnly: boolean;
  notifyDelay: number;
  notifyInChat: boolean;
  notifyOnlyWhenLive: boolean;
  youtubeSafe: boolean;
}

export interface GetSettingsQuery {
  currentUser: {
    id: string;
    settings: Settings;
  };
}

export interface SetSettingsInput {
  attributes: Partial<Settings>;
}

export interface SetSettingsMutation {
  updateSettings: {
    settings: Settings;
  };
}
