import * as React from 'react';
import { Button } from 'grommet';

interface ButtonProperties {
  onMore: () => void;
  loading: boolean;
}

export const LoadMoreButton = (props: ButtonProperties) => (
  <Button 
    style={{color: 'white'}}
    size="small"
    plain={false}
    primary={true}
    color="control"
    onClick={props.onMore}
    disabled={props.loading}>
    Load More
  </Button>
);
