import * as React from 'react';
import { Box } from 'grommet';
import { Night } from './SlobsColors';

interface PublicProps {
  circle?: boolean;
}

export class PlaceholderMusicCard extends React.Component<PublicProps> {
  public render() {
    return (
      <Box margin="15px" width="100%" style={{ position: 'relative', cursor: 'default' }}>
        <Box
          height="100%"
          width="100%"
          style={{
            position: 'relative',
            cursor: 'pointer',
            backgroundColor: Night.textMuted,
            borderRadius: this.props.circle ? '50%' : '0',
            flexShrink: 0,
          }}
        />
        <Box direction="column" height="90px" width="100%" />
      </Box>
    );
  }
}
