import React from 'react';
import { joinDomsGrammatically } from './Format';

it('renders 0 nodes', () => {
  const result = joinDomsGrammatically([]);
  expect(result).toEqual([]);
});

it('renders 1 node', () => {
  const one = <div>One</div>;
  const result = joinDomsGrammatically([one]);
  expect(result).toEqual([one]);
});

it('renders 2 nodes', () => {
  const one = <div>One</div>;
  const two = <div>Two</div>;
  const result = joinDomsGrammatically([one, two]);
  expect(result).toEqual([one, ' & ', two]);
});

it('renders 3 nodes', () => {
  const one = <div>One</div>;
  const two = <div>Two</div>;
  const three = <div>Three</div>;
  const result = joinDomsGrammatically([one, two, three]);
  expect(result).toEqual([one, ', ', two, ' & ', three]);
});
